static kal_uint16 read_cmos_sensor(kal_uint16 addr)
{
    kal_uint16 get_byte = 0;
    char pusendcmd[2] = {(char)(addr >> 8), (char)(addr & 0xFF) };

    iReadRegI2C(pusendcmd, 2, (u8 *)&get_byte, 1, imgsensor.i2c_write_id);
    return get_byte;
}

static void write_cmos_sensor(kal_uint16 addr, kal_uint8 para)
{
    char pusendcmd[3] = {(char)(addr >> 8), (char)(addr & 0xFF),
	    (char)(para & 0xFF)};
    iWriteRegI2C(pusendcmd, 3, imgsensor.i2c_write_id);
}

static void set_dummy(void)
{
	if (imgsensor.frame_length%2 != 0)
		imgsensor.frame_length =
			imgsensor.frame_length - imgsensor.frame_length%2;

	pr_debug("imgsensor.frame_length = %d\n", imgsensor.frame_length);
	write_cmos_sensor(0x380e, imgsensor.frame_length >> 8);
	write_cmos_sensor(0x380f, imgsensor.frame_length & 0xFF);
	write_cmos_sensor(0x380c, imgsensor.line_length >> 8);
	write_cmos_sensor(0x380d, imgsensor.line_length & 0xFF);

}

static void set_max_framerate(UINT16 framerate, kal_bool min_framelength_en)
{
    kal_uint32 frame_length = imgsensor.frame_length;

    pr_err("framerate = %d, min framelength should enable %d\n", framerate, min_framelength_en);

    frame_length = imgsensor.pclk / framerate * 10 / imgsensor.line_length;
    spin_lock(&imgsensor_drv_lock);
    if (frame_length >= imgsensor.min_frame_length)
	imgsensor.frame_length = frame_length;
    else
	imgsensor.frame_length = imgsensor.min_frame_length;

    imgsensor.dummy_line = imgsensor.frame_length - imgsensor.min_frame_length;

    if (imgsensor.frame_length > imgsensor_info.max_frame_length) {
	imgsensor.frame_length = imgsensor_info.max_frame_length;
	imgsensor.dummy_line = imgsensor.frame_length - imgsensor.min_frame_length;
    }
    if (min_framelength_en)
	imgsensor.min_frame_length = imgsensor.frame_length;
    spin_unlock(&imgsensor_drv_lock);
    set_dummy();
}    /*    set_max_framerate  */

static void write_shutter(kal_uint32 shutter)
{
    kal_uint16 realtime_fps = 0;

    spin_lock(&imgsensor_drv_lock);
    if (shutter > imgsensor.min_frame_length - imgsensor_info.margin) {
	imgsensor.frame_length = shutter + imgsensor_info.margin;
    } else {
	imgsensor.frame_length = imgsensor.min_frame_length;
    }
    if (imgsensor.frame_length > imgsensor_info.max_frame_length) {
	imgsensor.frame_length = imgsensor_info.max_frame_length;
    }
    spin_unlock(&imgsensor_drv_lock);
    if (shutter < imgsensor_info.min_shutter) {
	shutter = imgsensor_info.min_shutter;
    }
    if (shutter > (imgsensor_info.max_frame_length - imgsensor_info.margin)) {
	shutter = imgsensor_info.max_frame_length - imgsensor_info.margin;
    }
    if (imgsensor.autoflicker_en) {
	realtime_fps = imgsensor.pclk / imgsensor.line_length * 10 / imgsensor.frame_length;
	if (realtime_fps >= 297 && realtime_fps <= 305) {
	    realtime_fps = 296;
	    set_max_framerate(realtime_fps, 0);
	} else if (realtime_fps >= 147 && realtime_fps <= 150) {
	    realtime_fps = 146;
	    set_max_framerate(realtime_fps, 0);
	} else {
	  imgsensor.frame_length = ((imgsensor.frame_length + 1)  >> 1) << 1;
			write_cmos_sensor(0x380e, imgsensor.frame_length >> 8);
			write_cmos_sensor(0x380f, imgsensor.frame_length & 0xFF);

	}
	} else {
		/* Extend frame length*/
		imgsensor.frame_length = ((imgsensor.frame_length + 1)  >> 1) << 1;
		write_cmos_sensor(0x380e, imgsensor.frame_length >> 8);
		write_cmos_sensor(0x380f, imgsensor.frame_length & 0xFF);
	}

	write_cmos_sensor(0x3502, (shutter << 4) & 0xFF);
	write_cmos_sensor(0x3501, (shutter >> 4) & 0xFF);
	write_cmos_sensor(0x3500, (shutter >> 12) & 0x0F);

	pr_debug("Add for N3D! shutterlzl =%d, framelength =%d\n",
		shutter, imgsensor.frame_length);

}



/*************************************************************************
 * FUNCTION
 *	set_shutter
 *
 * DESCRIPTION
 *	This function set e-shutter of sensor to change exposure time.
 *
 * PARAMETERS
 *	iShutter : exposured lines
 *
 * RETURNS
 *	None
 *
 * GLOBALS AFFECTED
 *
 *************************************************************************/
static void set_shutter(kal_uint32 shutter)
{
    unsigned long flags;

    spin_lock_irqsave(&imgsensor_drv_lock, flags);
    imgsensor.shutter = shutter;
    spin_unlock_irqrestore(&imgsensor_drv_lock, flags);
    write_shutter(shutter);
} /* set_shutter */

static kal_uint16 gain2reg(const kal_uint16 gain)
{
    kal_uint16 reg_gain = 0x0;

    reg_gain = gain * 128 / BASEGAIN;

    if (reg_gain < 128) {
	reg_gain = 128;
    }
    if (reg_gain > 1984) {
	reg_gain = 1984;
    }
    return (kal_uint16) reg_gain;
}

static kal_uint16 set_gain(kal_uint16 gain)
{
    kal_uint16 reg_gain;

    reg_gain = gain2reg(gain);
    spin_lock(&imgsensor_drv_lock);
    imgsensor.gain = reg_gain;
    spin_unlock(&imgsensor_drv_lock);
    pr_err("gain = %d, reg_gain = 0x%x\n ", gain, reg_gain);
    write_cmos_sensor(0x3508, (reg_gain >> 8) & 0xFF);
    pr_err("OV8856 AE %04X %02X %02X\n", 0x3508, (reg_gain >> 8) & 0xFF, read_cmos_sensor(0x3508));
    write_cmos_sensor(0x3509, (reg_gain >> 0) & 0xFF);
    pr_err("OV8856 AE %04X %02X %02X\n", 0x3509, (reg_gain >> 0) & 0xFF, read_cmos_sensor(0x3509));

    return gain;
} /* set_gain */

static void set_shutter_frame_length(kal_uint16 shutter,
    kal_uint16 frame_length, kal_bool auto_extend_en)
{
    unsigned long flags;
    kal_int32 dummy_line = 0;

    spin_lock_irqsave(&imgsensor_drv_lock, flags);
    imgsensor.shutter = shutter;
    spin_unlock_irqrestore(&imgsensor_drv_lock, flags);
    spin_lock(&imgsensor_drv_lock);
    if (frame_length > 1)
	dummy_line = frame_length - imgsensor.frame_length;

    imgsensor.frame_length = imgsensor.frame_length + dummy_line;

    if (shutter > imgsensor.frame_length - imgsensor_info.margin)
	imgsensor.frame_length = shutter + imgsensor_info.margin;

    if (imgsensor.frame_length > imgsensor_info.max_frame_length)
	imgsensor.frame_length = imgsensor_info.max_frame_length;
    spin_unlock(&imgsensor_drv_lock);
    set_shutter(shutter);
}    /* set_shutter_frame_length */

static kal_uint32 streaming_control(kal_bool enable)
{
    pr_err("streaming_enable(0=Sw Standby,1=streaming): %d\n", enable);
    if (enable)
	write_cmos_sensor(0x0100, 0x01);
    else
	write_cmos_sensor(0x0100, 0x00);
    return ERROR_NONE;
}

static kal_uint16 get_vendor_id(void)
{
     kal_uint16 get_byte = 0;
     char pusendcmd[2] = {(char)(0x00 >> 8), (char)(0x01 & 0xFF) };

     iReadRegI2C(pusendcmd, 2, (u8 *)&get_byte, 1, 0xA2);

     return get_byte;
}

static kal_uint32 get_imgsensor_id(UINT32 *sensor_id)
{
    kal_uint8 i = 0;
    kal_uint8 retry = 2, vendor_id = 0;

    pr_err("cwd getimgsensorid!!!\n");

    vendor_id = get_vendor_id();
    if (vendor_id != 0x07) {
      pr_err("Vendor_id: 0x%x, this should be 0x07!\n", vendor_id);
      *sensor_id = 0xFFFFFFFF;
      return ERROR_SENSOR_CONNECT_FAIL;
    }
    while (imgsensor_info.i2c_addr_table[i] != 0xff) {
	spin_lock(&imgsensor_drv_lock);
	imgsensor.i2c_write_id = imgsensor_info.i2c_addr_table[i];
	spin_unlock(&imgsensor_drv_lock);
	do {
	if (vendor_id == 0x07) {
		*sensor_id = ((read_cmos_sensor(0x300B) << 8)
			| read_cmos_sensor(0x300C));
		if (*sensor_id == imgsensor_info.sensor_id) {
			pr_err("i2c write id: 0x%x, sensor id: 0x%x\n", imgsensor.i2c_write_id, *sensor_id);
			return ERROR_NONE;
		}
	}

	    pr_err("Read sensor id fail, id: 0x%x\n",    imgsensor.i2c_write_id);
	    retry--;
	} while (retry > 0);
	i++;
	retry = 2;
    }
    if (*sensor_id != imgsensor_info.sensor_id) {
	*sensor_id = 0xFFFFFFFF;
	return ERROR_SENSOR_CONNECT_FAIL;
    }
    return ERROR_NONE;
}

static kal_uint32 open(void)
{
    kal_uint8 i = 0;
    kal_uint8 retry = 2;
    kal_uint16 sensor_id = 0;

    pr_err("%s +\n", __func__);
    while (imgsensor_info.i2c_addr_table[i] != 0xff) {
	spin_lock(&imgsensor_drv_lock);
	imgsensor.i2c_write_id = imgsensor_info.i2c_addr_table[i];
	spin_unlock(&imgsensor_drv_lock);
	do {
	    sensor_id = ((read_cmos_sensor(0x300B) << 8)
		    | read_cmos_sensor(0x300C));
	    if (sensor_id == imgsensor_info.sensor_id) {
		pr_err("i2c write id: 0x%x, sensor id: 0x%x\n",
		    imgsensor.i2c_write_id, sensor_id);
		break;
	    }
	    pr_err("Read sensor id fail, id: 0x%x\n",
		imgsensor.i2c_write_id);
	    retry--;
	} while (retry > 0);
	i++;
	if (sensor_id == imgsensor_info.sensor_id)
	    break;
	retry = 2;
    }
    if (imgsensor_info.sensor_id != sensor_id)
	return ERROR_SENSOR_CONNECT_FAIL;

    load_init_setting();

    spin_lock(&imgsensor_drv_lock);

    imgsensor.autoflicker_en = KAL_FALSE;
    imgsensor.sensor_mode = IMGSENSOR_MODE_INIT;
    imgsensor.shutter = 0x3D0;
    imgsensor.gain = 0x100;
    imgsensor.pclk = imgsensor_info.pre.pclk;
    imgsensor.frame_length = imgsensor_info.pre.framelength;
    imgsensor.line_length = imgsensor_info.pre.linelength;
    imgsensor.min_frame_length = imgsensor_info.pre.framelength;
    imgsensor.dummy_pixel = 0;
    imgsensor.dummy_line = 0;
    imgsensor.ihdr_mode = 0;
    imgsensor.test_pattern = KAL_FALSE;
    imgsensor.current_fps = imgsensor_info.pre.max_framerate;
    spin_unlock(&imgsensor_drv_lock);
    pr_err("%s -\n", __func__);

    return ERROR_NONE;
} /* open */

static kal_uint32 close(void)
{
    pr_err("E\n");
    streaming_control(KAL_FALSE);
    return ERROR_NONE;
} /* close */

