static kal_uint32 get_resolution(
    MSDK_SENSOR_RESOLUTION_INFO_STRUCT * sensor_resolution)
{
    pr_err("E\n");
    sensor_resolution->SensorFullWidth =
	imgsensor_info.cap.grabwindow_width;
    sensor_resolution->SensorFullHeight =
	imgsensor_info.cap.grabwindow_height;

    sensor_resolution->SensorPreviewWidth =
	imgsensor_info.pre.grabwindow_width;
    sensor_resolution->SensorPreviewHeight =
	imgsensor_info.pre.grabwindow_height;

    sensor_resolution->SensorVideoWidth =
	imgsensor_info.normal_video.grabwindow_width;
    sensor_resolution->SensorVideoHeight =
	imgsensor_info.normal_video.grabwindow_height;

    sensor_resolution->SensorHighSpeedVideoWidth =
	imgsensor_info.hs_video.grabwindow_width;
    sensor_resolution->SensorHighSpeedVideoHeight =
	imgsensor_info.hs_video.grabwindow_height;

    sensor_resolution->SensorSlimVideoWidth =
	imgsensor_info.slim_video.grabwindow_width;
    sensor_resolution->SensorSlimVideoHeight =
	imgsensor_info.slim_video.grabwindow_height;

    return ERROR_NONE;
} /* get_resolution */

static kal_uint32 get_info(enum MSDK_SCENARIO_ID_ENUM scenario_id,
    MSDK_SENSOR_INFO_STRUCT *sensor_info,
    MSDK_SENSOR_CONFIG_STRUCT *sensor_config_data)
{
    pr_err("scenario_id = %d\n", scenario_id);

    sensor_info->SensorClockPolarity = SENSOR_CLOCK_POLARITY_LOW;
    sensor_info->SensorClockFallingPolarity = SENSOR_CLOCK_POLARITY_LOW;
    sensor_info->SensorHsyncPolarity = SENSOR_CLOCK_POLARITY_LOW;
    sensor_info->SensorVsyncPolarity = SENSOR_CLOCK_POLARITY_LOW;
    sensor_info->SensorInterruptDelayLines = 4; /* not use */
    sensor_info->SensorResetActiveHigh = FALSE; /* not use */
    sensor_info->SensorResetDelayCount = 5; /* not use */

    sensor_info->SensroInterfaceType = imgsensor_info.sensor_interface_type;
    sensor_info->MIPIsensorType = imgsensor_info.mipi_sensor_type;
    sensor_info->SettleDelayMode = imgsensor_info.mipi_settle_delay_mode;
    sensor_info->SensorOutputDataFormat =
	imgsensor_info.sensor_output_dataformat;

    sensor_info->CaptureDelayFrame = imgsensor_info.cap_delay_frame;
    sensor_info->PreviewDelayFrame = imgsensor_info.pre_delay_frame;
    sensor_info->VideoDelayFrame = imgsensor_info.video_delay_frame;
    sensor_info->HighSpeedVideoDelayFrame =
	imgsensor_info.hs_video_delay_frame;
    sensor_info->SlimVideoDelayFrame =
	imgsensor_info.slim_video_delay_frame;

    sensor_info->SensorMasterClockSwitch = 0; /* not use */
    sensor_info->SensorDrivingCurrent = imgsensor_info.isp_driving_current;

    sensor_info->AEShutDelayFrame = imgsensor_info.ae_shut_delay_frame;
    sensor_info->AESensorGainDelayFrame =
	imgsensor_info.ae_sensor_gain_delay_frame;
    sensor_info->AEISPGainDelayFrame =
	imgsensor_info.ae_ispGain_delay_frame;
    sensor_info->IHDR_Support = imgsensor_info.ihdr_support;
    sensor_info->IHDR_LE_FirstLine = imgsensor_info.ihdr_le_firstline;
    sensor_info->SensorModeNum = imgsensor_info.sensor_mode_num;
    sensor_info->PDAF_Support = 0;
    sensor_info->SensorMIPILaneNumber = imgsensor_info.mipi_lane_num;
    sensor_info->SensorClockFreq = imgsensor_info.mclk;
    sensor_info->SensorClockDividCount = 3; /* not use */
    sensor_info->SensorClockRisingCount = 0;
    sensor_info->SensorClockFallingCount = 2; /* not use */
    sensor_info->SensorPixelClockCount = 3; /* not use */
    sensor_info->SensorDataLatchCount = 2; /* not use */

    sensor_info->MIPIDataLowPwr2HighSpeedTermDelayCount = 0;
    sensor_info->MIPICLKLowPwr2HighSpeedTermDelayCount = 0;
    sensor_info->SensorWidthSampling = 0; /* 0 is default 1x */
    sensor_info->SensorHightSampling = 0; /* 0 is default 1x */
    sensor_info->SensorPacketECCOrder = 1;

    sensor_info->FrameTimeDelayFrame = imgsensor_info.frame_time_delay_frame;

    switch (scenario_id) {
    case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	sensor_info->SensorGrabStartX = imgsensor_info.pre.startx;
	sensor_info->SensorGrabStartY = imgsensor_info.pre.starty;
	sensor_info->MIPIDataLowPwr2HighSpeedSettleDelayCount =
	    imgsensor_info.pre.mipi_data_lp2hs_settle_dc;
	break;
    case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	sensor_info->SensorGrabStartX = imgsensor_info.cap.startx;
	sensor_info->SensorGrabStartY = imgsensor_info.cap.starty;
	sensor_info->MIPIDataLowPwr2HighSpeedSettleDelayCount =
	    imgsensor_info.cap.mipi_data_lp2hs_settle_dc;
	break;
    case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	sensor_info->SensorGrabStartX =    imgsensor_info.normal_video.startx;
	sensor_info->SensorGrabStartY =    imgsensor_info.normal_video.starty;
	sensor_info->MIPIDataLowPwr2HighSpeedSettleDelayCount =
	    imgsensor_info.normal_video.mipi_data_lp2hs_settle_dc;

	break;
    case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	sensor_info->SensorGrabStartX = imgsensor_info.hs_video.startx;
	sensor_info->SensorGrabStartY = imgsensor_info.hs_video.starty;
	sensor_info->MIPIDataLowPwr2HighSpeedSettleDelayCount =
	    imgsensor_info.hs_video.mipi_data_lp2hs_settle_dc;
	break;
    case MSDK_SCENARIO_ID_SLIM_VIDEO:
	sensor_info->SensorGrabStartX =    imgsensor_info.slim_video.startx;
	sensor_info->SensorGrabStartY =    imgsensor_info.slim_video.starty;
	sensor_info->MIPIDataLowPwr2HighSpeedSettleDelayCount =
	    imgsensor_info.slim_video.mipi_data_lp2hs_settle_dc;
	break;
    default:
	sensor_info->SensorGrabStartX = imgsensor_info.pre.startx;
	sensor_info->SensorGrabStartY = imgsensor_info.pre.starty;
	sensor_info->MIPIDataLowPwr2HighSpeedSettleDelayCount =
	    imgsensor_info.pre.mipi_data_lp2hs_settle_dc;
	break;
    }

    return ERROR_NONE;
}    /*    get_info  */


static kal_uint32 control(enum MSDK_SCENARIO_ID_ENUM scenario_id,
    MSDK_SENSOR_EXPOSURE_WINDOW_STRUCT *image_window,
    MSDK_SENSOR_CONFIG_STRUCT *sensor_config_data)
{
    pr_err("scenario_id = %d\n", scenario_id);
    spin_lock(&imgsensor_drv_lock);
    imgsensor.current_scenario_id = scenario_id;
    spin_unlock(&imgsensor_drv_lock);
    switch (scenario_id) {
    case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	preview(image_window, sensor_config_data);
	break;
    case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	capture(image_window, sensor_config_data);
	break;
    case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	normal_video(image_window, sensor_config_data);
	break;
    case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	hs_video(image_window, sensor_config_data);
	break;
    case MSDK_SCENARIO_ID_SLIM_VIDEO:
	slim_video(image_window, sensor_config_data);
	break;
    default:
	pr_err("Error ScenarioId setting");
	preview(image_window, sensor_config_data);
	return ERROR_INVALID_SCENARIO_ID;
    }

    return ERROR_NONE;
}    /* control() */

static kal_uint32 set_video_mode(UINT16 framerate)
{
    pr_err("framerate = %d\n ", framerate);
    if (framerate == 0)
	return ERROR_NONE;
    spin_lock(&imgsensor_drv_lock);
    if ((framerate == 300) && (imgsensor.autoflicker_en == KAL_TRUE))
	imgsensor.current_fps = 296;
    else if ((framerate == 150) && (imgsensor.autoflicker_en == KAL_TRUE))
	imgsensor.current_fps = 146;
    else
	imgsensor.current_fps = framerate;
    spin_unlock(&imgsensor_drv_lock);
    set_max_framerate(imgsensor.current_fps, 1);

    return ERROR_NONE;
}

static kal_uint32 set_auto_flicker_mode(kal_bool enable, UINT16 framerate)
{
    spin_lock(&imgsensor_drv_lock);
    if (enable) {
	imgsensor.autoflicker_en = KAL_TRUE;
	pr_err("enable! fps = %d", framerate);
    } else {
	imgsensor.autoflicker_en = KAL_FALSE;
    }
    spin_unlock(&imgsensor_drv_lock);

    return ERROR_NONE;
}

static kal_uint32 set_max_framerate_by_scenario(
	enum MSDK_SCENARIO_ID_ENUM scenario_id, MUINT32 framerate)
{
    kal_uint32 frame_length;

    pr_err("scenario_id = %d, framerate = %d\n", scenario_id, framerate);

    switch (scenario_id) {
    case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	frame_length = imgsensor_info.pre.pclk / framerate * 10
	    / imgsensor_info.pre.linelength;
	spin_lock(&imgsensor_drv_lock);
	imgsensor.dummy_line = (frame_length > imgsensor_info.pre.framelength)
	    ? (frame_length - imgsensor_info.pre.framelength) : 0;
	imgsensor.frame_length = imgsensor_info.pre.framelength
	    + imgsensor.dummy_line;
	imgsensor.min_frame_length = imgsensor.frame_length;
	spin_unlock(&imgsensor_drv_lock);
	if (imgsensor.frame_length > imgsensor.shutter)
	    set_dummy();
	break;
    case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	if (framerate == 0)
	    return ERROR_NONE;
	frame_length = imgsensor_info.normal_video.pclk /
	    framerate * 10 / imgsensor_info.normal_video.linelength;
	spin_lock(&imgsensor_drv_lock);
	imgsensor.dummy_line = (frame_length > imgsensor_info.normal_video.framelength)
	    ? (frame_length - imgsensor_info.normal_video.framelength) : 0;
	imgsensor.frame_length = imgsensor_info.normal_video.framelength
	    + imgsensor.dummy_line;
	imgsensor.min_frame_length = imgsensor.frame_length;
	spin_unlock(&imgsensor_drv_lock);
	if (imgsensor.frame_length > imgsensor.shutter)
	    set_dummy();
	break;
    case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	if (imgsensor.current_fps != imgsensor_info.cap.max_framerate)
	    pr_err(
		"Warning: current_fps %d fps is not support, so use cap's setting: %d fps!\n"
		, framerate, imgsensor_info.cap.max_framerate/10);
	frame_length = imgsensor_info.cap.pclk / framerate * 10
	    / imgsensor_info.cap.linelength;
	spin_lock(&imgsensor_drv_lock);
	imgsensor.dummy_line = (frame_length > imgsensor_info.cap.framelength)
	    ? (frame_length - imgsensor_info.cap.framelength) : 0;
	imgsensor.frame_length = imgsensor_info.cap.framelength
	    + imgsensor.dummy_line;
	imgsensor.min_frame_length = imgsensor.frame_length;
	spin_unlock(&imgsensor_drv_lock);

	if (imgsensor.frame_length > imgsensor.shutter)
	    set_dummy();
	break;
    case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	frame_length = imgsensor_info.hs_video.pclk / framerate * 10
		/ imgsensor_info.hs_video.linelength;
	spin_lock(&imgsensor_drv_lock);
	imgsensor.dummy_line =
	    (frame_length > imgsensor_info.hs_video.framelength)
	    ? (frame_length - imgsensor_info.hs_video.framelength)
	    : 0;
	imgsensor.frame_length =
	    imgsensor_info.hs_video.framelength
	    + imgsensor.dummy_line;
	imgsensor.min_frame_length = imgsensor.frame_length;
	spin_unlock(&imgsensor_drv_lock);
	if (imgsensor.frame_length > imgsensor.shutter)
	    set_dummy();
	break;
    case MSDK_SCENARIO_ID_SLIM_VIDEO:
	frame_length = imgsensor_info.slim_video.pclk / framerate * 10
	    / imgsensor_info.slim_video.linelength;
	spin_lock(&imgsensor_drv_lock);
	imgsensor.dummy_line = (frame_length > imgsensor_info.slim_video.framelength)
	    ? (frame_length - imgsensor_info.slim_video.framelength) : 0;
	imgsensor.frame_length = imgsensor_info.slim_video.framelength
	    + imgsensor.dummy_line;
	imgsensor.min_frame_length = imgsensor.frame_length;
	spin_unlock(&imgsensor_drv_lock);
	if (imgsensor.frame_length > imgsensor.shutter)
	    set_dummy();
	break;
    default:  /*coding with  preview scenario by default*/
	frame_length = imgsensor_info.pre.pclk / framerate * 10
	    / imgsensor_info.pre.linelength;
	spin_lock(&imgsensor_drv_lock);
	imgsensor.dummy_line =
	    (frame_length > imgsensor_info.pre.framelength)
	    ? (frame_length - imgsensor_info.pre.framelength) : 0;
	imgsensor.frame_length = imgsensor_info.pre.framelength
	    + imgsensor.dummy_line;
	imgsensor.min_frame_length = imgsensor.frame_length;
	spin_unlock(&imgsensor_drv_lock);
	if (imgsensor.frame_length > imgsensor.shutter)
	    set_dummy();
	pr_err("error scenario_id = %d, we use preview scenario\n",
	    scenario_id);
	break;
    }
    return ERROR_NONE;
}

static kal_uint32 get_default_framerate_by_scenario(
	enum MSDK_SCENARIO_ID_ENUM scenario_id, MUINT32 *framerate)
{
    pr_err("scenario_id = %d\n", scenario_id);

    switch (scenario_id) {
    case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	*framerate = imgsensor_info.pre.max_framerate;
	break;
    case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	*framerate = imgsensor_info.normal_video.max_framerate;
	break;
    case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	*framerate = imgsensor_info.cap.max_framerate;
	break;
    case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	*framerate = imgsensor_info.hs_video.max_framerate;
	break;
    case MSDK_SCENARIO_ID_SLIM_VIDEO:
	*framerate = imgsensor_info.slim_video.max_framerate;
	break;
    default:
	break;
    }

    return ERROR_NONE;
}

static kal_uint32 set_test_pattern_mode(kal_bool enable)
{
    pr_err("enable: %d\n", enable);

    if (enable)
	write_cmos_sensor(0x5e00, 0x80);//enable
    else
	write_cmos_sensor(0x5e00, 0x00);//disable

    spin_lock(&imgsensor_drv_lock);
    imgsensor.test_pattern = enable;
    spin_unlock(&imgsensor_drv_lock);
    return ERROR_NONE;
}

