static kal_uint32 feature_control(MSDK_SENSOR_FEATURE_ENUM feature_id,
    UINT8 *feature_para, UINT32 *feature_para_len)
{
    UINT16 *feature_return_para_16 = (UINT16 *) feature_para;
    UINT16 *feature_data_16 = (UINT16 *) feature_para;
    UINT32 *feature_return_para_32 = (UINT32 *) feature_para;
    UINT32 *feature_data_32 = (UINT32 *) feature_para;
    unsigned long long *feature_data = (unsigned long long *) feature_para;
    struct SENSOR_WINSIZE_INFO_STRUCT *wininfo;

    MSDK_SENSOR_REG_INFO_STRUCT *sensor_reg_data
	= (MSDK_SENSOR_REG_INFO_STRUCT *) feature_para;

    /*pr_err("feature_id = %d\n", feature_id);*/
    switch (feature_id) {
    /*ITS sensor_fusion: Best correlation of 0.000011 at shift of 3.35ms*/
    /*default shift is 3,the current shift is (3+3.35)*1000000*/
    case SENSOR_FEATURE_GET_ANA_GAIN_TABLE:
	if ((void *)(uintptr_t) (*(feature_data + 1)) == NULL
		|| *(feature_data + 0) == 0) {
			*(feature_data + 0) =
				sizeof(ana_gain_table_16x);
	} else {
		memcpy((void *)(uintptr_t) (*(feature_data + 1)),
			(void *)ana_gain_table_16x,
			sizeof(ana_gain_table_16x));
	}
	break;
    case SENSOR_FEATURE_GET_OFFSET_TO_START_OF_EXPOSURE:
	*(MUINT32 *)(uintptr_t)(*(feature_data + 1)) = 6350000;
	break;
    case SENSOR_FEATURE_GET_AWB_REQ_BY_SCENARIO:
	*(MUINT32 *)(uintptr_t)(*(feature_data + 1)) = 0;
	break;
    case SENSOR_FEATURE_GET_GAIN_RANGE_BY_SCENARIO:
	*(feature_data + 1) = imgsensor_info.min_gain;
	*(feature_data + 2) = imgsensor_info.max_gain;
	break;
    case SENSOR_FEATURE_GET_BASE_GAIN_ISO_AND_STEP:
	*(feature_data + 0) = imgsensor_info.min_gain_iso;
	*(feature_data + 1) = imgsensor_info.gain_step;
	*(feature_data + 2) = imgsensor_info.gain_type;
	break;
    case SENSOR_FEATURE_GET_MIN_SHUTTER_BY_SCENARIO:
	*(feature_data + 1) = imgsensor_info.min_shutter;
	*(feature_data + 2) = imgsensor_info.exp_step;
	break;
    case SENSOR_FEATURE_GET_PIXEL_CLOCK_FREQ_BY_SCENARIO:
	switch (*feature_data) {
	case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.cap.pclk;
	    break;
	case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.normal_video.pclk;
	    break;
	case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.hs_video.pclk;
	    break;
	case MSDK_SCENARIO_ID_SLIM_VIDEO:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.slim_video.pclk;
	    break;
	case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	default:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.pre.pclk;
	    break;
	}
	break;
    case SENSOR_FEATURE_GET_PERIOD_BY_SCENARIO:
	switch (*feature_data) {
	case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
	    = (imgsensor_info.cap.framelength << 16)
		+ imgsensor_info.cap.linelength;
	    break;
	case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
	    = (imgsensor_info.normal_video.framelength << 16)
		+ imgsensor_info.normal_video.linelength;
	    break;
	case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
	    = (imgsensor_info.hs_video.framelength << 16)
		+ imgsensor_info.hs_video.linelength;
	    break;
	case MSDK_SCENARIO_ID_SLIM_VIDEO:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
	    = (imgsensor_info.slim_video.framelength << 16)
		+ imgsensor_info.slim_video.linelength;
	    break;
	case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	default:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
	    = (imgsensor_info.pre.framelength << 16)
		+ imgsensor_info.pre.linelength;
	    break;
	}
	break;

    case SENSOR_FEATURE_GET_PERIOD:
	*feature_return_para_16++ = imgsensor.line_length;
	*feature_return_para_16 = imgsensor.frame_length;
	*feature_para_len = 4;
	break;
    case SENSOR_FEATURE_GET_PIXEL_CLOCK_FREQ:
	*feature_return_para_32 = imgsensor.pclk;
	*feature_para_len = 4;
	break;
    case SENSOR_FEATURE_SET_ESHUTTER:
	 set_shutter(*feature_data);
	break;
    case SENSOR_FEATURE_SET_NIGHTMODE:
	//night_mode((BOOL) (*feature_data));
	break;
    case SENSOR_FEATURE_SET_GAIN:
	set_gain((UINT16) *feature_data);
	break;
    case SENSOR_FEATURE_SET_FLASHLIGHT:
	break;
    case SENSOR_FEATURE_SET_ISP_MASTER_CLOCK_FREQ:
	break;
    case SENSOR_FEATURE_SET_REGISTER:
	write_cmos_sensor(sensor_reg_data->RegAddr,
		    sensor_reg_data->RegData);
	break;
    case SENSOR_FEATURE_GET_REGISTER:
	sensor_reg_data->RegData =
	    read_cmos_sensor(sensor_reg_data->RegAddr);
	break;
    case SENSOR_FEATURE_GET_LENS_DRIVER_ID:
	*feature_return_para_32 = LENS_DRIVER_ID_DO_NOT_CARE;
	*feature_para_len = 4;
	break;
    case SENSOR_FEATURE_SET_VIDEO_MODE:
	set_video_mode(*feature_data);
	break;
    case SENSOR_FEATURE_CHECK_SENSOR_ID:
	get_imgsensor_id(feature_return_para_32);
	break;
    case SENSOR_FEATURE_SET_AUTO_FLICKER_MODE:
	set_auto_flicker_mode((BOOL)*feature_data_16,
		      *(feature_data_16+1));
	break;
    case SENSOR_FEATURE_SET_MAX_FRAME_RATE_BY_SCENARIO:
	 set_max_framerate_by_scenario(
		(enum MSDK_SCENARIO_ID_ENUM)*feature_data,
		*(feature_data+1));
	break;
    case SENSOR_FEATURE_GET_DEFAULT_FRAME_RATE_BY_SCENARIO:
	 get_default_framerate_by_scenario(
		(enum MSDK_SCENARIO_ID_ENUM)*(feature_data),
		(MUINT32 *)(uintptr_t)(*(feature_data+1)));
	break;
    case SENSOR_FEATURE_SET_TEST_PATTERN:
	set_test_pattern_mode((BOOL)*feature_data);
	break;
    case SENSOR_FEATURE_GET_TEST_PATTERN_CHECKSUM_VALUE:
	*feature_return_para_32 = imgsensor_info.checksum_value;
	*feature_para_len = 4;
	break;
    case SENSOR_FEATURE_SET_FRAMERATE:
	pr_err("current fps :%d\n", (UINT32)*feature_data_32);
	spin_lock(&imgsensor_drv_lock);
	imgsensor.current_fps = *feature_data_32;
	spin_unlock(&imgsensor_drv_lock);
	break;
    case SENSOR_FEATURE_SET_HDR:
	pr_err("ihdr enable :%d\n", (BOOL)*feature_data_32);
	spin_lock(&imgsensor_drv_lock);
	imgsensor.ihdr_mode = *feature_data_32;
	spin_unlock(&imgsensor_drv_lock);
	break;
    case SENSOR_FEATURE_GET_CROP_INFO:
	pr_err("SENSOR_FEATURE_GET_CROP_INFO scenarioId:%d\n",
	    (UINT32)*feature_data);
	wininfo = (struct SENSOR_WINSIZE_INFO_STRUCT *)(uintptr_t)(*(feature_data+1));

	switch (*feature_data_32) {
	case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	    memcpy((void *)wininfo,
		(void *)&imgsensor_winsize_info[1],
		sizeof(struct SENSOR_WINSIZE_INFO_STRUCT));
	    break;
	case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	    memcpy((void *)wininfo,
		(void *)&imgsensor_winsize_info[2],
		sizeof(struct SENSOR_WINSIZE_INFO_STRUCT));
	    break;
	case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	    memcpy((void *)wininfo,
	    (void *)&imgsensor_winsize_info[3],
	    sizeof(struct SENSOR_WINSIZE_INFO_STRUCT));
	    break;
	case MSDK_SCENARIO_ID_SLIM_VIDEO:
	    memcpy((void *)wininfo,
	    (void *)&imgsensor_winsize_info[4],
	    sizeof(struct SENSOR_WINSIZE_INFO_STRUCT));
	    break;
	case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	default:
	    memcpy((void *)wininfo,
	    (void *)&imgsensor_winsize_info[0],
	    sizeof(struct SENSOR_WINSIZE_INFO_STRUCT));
	    break;
	}
	break;
    case SENSOR_FEATURE_SET_IHDR_SHUTTER_GAIN:
	pr_err("SENSOR_SET_SENSOR_IHDR LE=%d, SE=%d, Gain=%d\n",
	    (UINT16)*feature_data,
	    (UINT16)*(feature_data+1),
	    (UINT16)*(feature_data+2));
	//ihdr_write_shutter_gain((UINT16) *feature_data,
	//            (UINT16) *(feature_data + 1),
	//            (UINT16) *(feature_data + 2));
	break;
    case SENSOR_FEATURE_SET_SHUTTER_FRAME_TIME:
	set_shutter_frame_length((UINT16) (*feature_data),
		    (UINT16) (*(feature_data + 1)),
		    (BOOL) (*(feature_data + 2)));
	break;
    case SENSOR_FEATURE_GET_FRAME_CTRL_INFO_BY_SCENARIO:
	*(feature_data + 1) = 1;
	*(feature_data + 2) = imgsensor_info.margin;
	break;
    case SENSOR_FEATURE_SET_HDR_SHUTTER:
	pr_err("SENSOR_FEATURE_SET_HDR_SHUTTER LE=%d, SE=%d\n",
	    (UINT16)*feature_data, (UINT16)*(feature_data+1));
	//ihdr_write_shutter_gain((UINT16) *feature_data,
	//            (UINT16) *(feature_data + 1),
	//            (UINT16) *(feature_data + 2));
	break;
    case SENSOR_FEATURE_SET_STREAMING_SUSPEND:
	pr_err("SENSOR_FEATURE_SET_STREAMING_SUSPEND\n");
	streaming_control(KAL_FALSE);
	break;
    case SENSOR_FEATURE_SET_STREAMING_RESUME:
	pr_err("SENSOR_FEATURE_SET_STREAMING_RESUME, shutter:%llu\n",
	    *feature_data);
	if (*feature_data != 0)
	    set_shutter(*feature_data);
	streaming_control(KAL_TRUE);
	break;
    case SENSOR_FEATURE_GET_BINNING_TYPE:
	switch (*(feature_data + 1)) {
	case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	case MSDK_SCENARIO_ID_SLIM_VIDEO:
	case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	default:
	    *feature_return_para_32 = 1; /*BINNING_AVERAGED*/
	    break;
	}
	pr_err("SENSOR_FEATURE_GET_BINNING_TYPE AE_binning_type:%d,\n",
	    *feature_return_para_32);
	*feature_para_len = 4;

	break;
    case SENSOR_FEATURE_GET_MIPI_PIXEL_RATE:
	switch (*feature_data) {
	case MSDK_SCENARIO_ID_CAMERA_CAPTURE_JPEG:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.cap.mipi_pixel_rate;
	    break;
	case MSDK_SCENARIO_ID_VIDEO_PREVIEW:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.normal_video.mipi_pixel_rate;
	    break;
	case MSDK_SCENARIO_ID_HIGH_SPEED_VIDEO:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.hs_video.mipi_pixel_rate;
	    break;
	case MSDK_SCENARIO_ID_SLIM_VIDEO:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.slim_video.mipi_pixel_rate;
	    break;
	case MSDK_SCENARIO_ID_CAMERA_PREVIEW:
	default:
	    *(MUINT32 *)(uintptr_t)(*(feature_data + 1))
		= imgsensor_info.pre.mipi_pixel_rate;
	    break;
	}
	break;

    case SENSOR_FEATURE_SET_AWB_GAIN:
	break;
    case SENSOR_FEATURE_SET_LSC_TBL:
	break;
    default:
	break;
    }

    return ERROR_NONE;
} /* feature_control() */

static struct SENSOR_FUNCTION_STRUCT sensor_func = {
    open,
    get_info,
    get_resolution,
    feature_control,
    control,
    close
};

UINT32 OV8856_MIPI_RAW_SensorInit(struct SENSOR_FUNCTION_STRUCT **pfFunc)
{
    if (pfFunc != NULL)
	*pfFunc = &sensor_func;
    return ERROR_NONE;
} /* OV8856_MIPI_RAW_SensorInit */

